mapper.select:
		SELECT 
		username `username`,
		password `password`,
		email `email`
		FROM USER 
		WHERE 1=1

            AND username = #{username}


            AND password = #{password}


            AND email = #{email}

mapper.update:
		UPDATE USER  
		SET 

            email = #{email} ,


            password = #{password} 

		WHERE username=#{username}
